mapper.select:
select gdsNum, gdsName, cateCode, gdsPrice, gdsStock, gdsDes, gdsImg, gdsDate
    from wmk_goods
        where gdsNum = #{gdsNum}

mapper.update:
update tbl_goods
	set gdsName = #{gdsName}, cateCode = #{cateCode}, gdsPrice = #{gdsPrice}, gdsStock = #{gdsStock}, gdsDes = #{gdsDes},
		gdsImg = #{gdsImg}, gdsThumbImg = #{gdsThumbImg} 
			where gdsNum = ${gdsNum}
